//Component to search for meal by ingredient
import React, { useState } from 'react';
import Recipes from './Recipes';

function SearchForm() {

        const [searchMeal, setSearchMeal] = useState(null)

    function getMeals() {
        fetch(`www.themealdb.com/api/json/v1/1/list.php?i=list`)
        .then((response) => response.json())
        .then((data) => {
            setSearchMeal(data);
        })
        .catch(() => {
            console.log("error");
        });
    }


    return (
        <div className="navBar">
        <form className = "form-horizontal">
            <input placeholder="Search for a recipe. . ."
                   type="text"
                   name="search"
                   value={searchMeal}
                   onChange={(e) => setSearchMeal(e.target.value)}
                   />
            <button type="submit" onClick={getMeals}>Search</button>
        </form>
        {searchMeal && <Recipes searchMeal={searchMeal} />}
        </div>
    );
}


export default SearchForm;
