import React, { useEffect } from 'react';

const mealdburl = 'https://www.themealdb.com/api/json/v1/1/filter.php?c='

const CategoryMeals = ({ match, constMeals, setConstMeals}) => {

    useEffect(() => {
        const categories = match.params.meals
        const url = `${mealdburl}${categories}`;

        fetch(url)
        .then(res => res.json())
        .then(res => {
            let thisCat = res.data.meals[categories];
            setConstMeals(thisCat);
        })
        .catch(err => {
            console.error(err);
        });
    }, [])

    return (

        <div className="CategoryMeals">
                <p>{match.params.categories}</p>
                <div className="idk">{constMeals}</div>
        </div>

    )
}

export default CategoryMeals;
