import React from 'react';


const SearchResults = ({ images }) => {

    if (!images) {
        return <h2>No meals found!</h2>
    }

return (
    <div className="gallery">
        {images.map((recipe) => {
         const {idMeal, strMeal, strArea, strInstructions, strMealThumb} = recipe

         return (
           <article className="mealContainer" key={strMeal}>
             <h2>{strMeal}</h2>
             <h3>({strArea})</h3>
             <img src={strMealThumb} alt={strMeal} />
             <p>{strInstructions}</p>
           </article>
         )
       })}
       </div>
    )
}

export default SearchResults;
