import React, { useEffect, useState } from 'react';

function Main() {

    const [random, setRandom] = useState()
    const url = "www.themealdb.com/api/json/v1/1/random.php";

      const getRandomRecipe = () => {
      fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setRandom(res)
      })
    }

    useEffect(() => {
      getRandomRecipe()
    }, [])



  return (
    <div className="Main">
        <h1>{random.meals.strMeal}</h1>
    </div>
  );
}

export default Main;
