import React, { useState, useEffect } from 'react';

const url = 'https://www.themealdb.com/api/json/v1/1/random.php'

const Main = () => {
  const [recipe, setRecipe] = useState([])

  useEffect(() => {
    fetch(url)
    .then((res) => res.json())
    .then((json) => {
      setRecipe(json);
      console.log(json)
    })
    .catch(console.error);
  }, []);

  return (
    <section className="container">
      <h1>{recipe.meals.strMeal}</h1>
    </section>
  );
}


export default Main;














