import React, { useEffect, useState } from 'react';
import Recipes from './Recipes';

function Main() {
  const [mealData, setMealData] = useState(null);

    function getRecipes() {
    const url = "www.themealdb.com/api/json/v1/1/random.php"
    fetch(url)
    .then((res) => res.json())
    .then((data) => {
      setMealData(data);
      console.log(data);
    })
    .catch(() => {
      console.log("error");
    });


  return (
    <div className="Main">
      {mealData && <Recipes getRecipes={mealData} />}
    </div>
  );
}
}

export default Main;
